var now = moment();
var task = {
  msg: 'Heeeellllooooo!',
  time: now.add(5, 's')
}


var seconds = 0;
setInterval(function tick() {
  console.log(++seconds);
  // console.log(task.time);
  var now = moment();
  var buffer = task.time.clone().add(1, 'm');
  if (now.isBetween(task.time, buffer)) {
    console.log(task.msg);
  }
}, 1000)

process.stdin.resume();
